"use strict";

const EventImpl = require("jsdom/lib/jsdom/living/events/Event-impl").implementation;

const CustomEventInit = require("jsdom/lib/jsdom/living/generated/CustomEventInit");

class CustomEventImpl extends EventImpl {
  initCustomEvent(type, bubbles, cancelable, detail) {
    if (this._dispatchFlag) {
      return;
    }

    this.initEvent(type, bubbles, cancelable);
    this.detail = detail;
  }
}
CustomEventImpl.defaultInit = CustomEventInit.convert(undefined, undefined);

module.exports = {
  implementation: CustomEventImpl
};
