"use strict";

const EventImpl = require("jsdom/lib/jsdom/living/events/Event-impl").implementation;

const MessageEventInit = require("jsdom/lib/jsdom/living/generated/MessageEventInit");

class MessageEventImpl extends EventImpl {
  initMessageEvent(type, bubbles, cancelable, data, origin, lastEventId, source, ports) {
    if (this._dispatchFlag) {
      return;
    }

    this.initEvent(type, bubbles, cancelable);
    this.data = data;
    this.origin = origin;
    this.lastEventId = lastEventId;
    this.source = source;
    this.ports = ports;
  }
}
MessageEventImpl.defaultInit = MessageEventInit.convert(undefined, undefined);

module.exports = {
  implementation: MessageEventImpl
};
