"use strict";
const HTMLElementImpl = require("jsdom/lib/jsdom/living/nodes/HTMLElement-impl").implementation;
const { formOwner } = require("jsdom/lib/jsdom/living/helpers/form-controls");
const { HTML_NS } = require("jsdom/lib/jsdom/living/helpers/namespaces");

class HTMLLegendElementImpl extends HTMLElementImpl {
  get form() {
    const parent = this.parentNode;
    if (parent && parent._localName === "fieldset" && parent.namespaceURI === HTML_NS) {
      return formOwner(parent);
    }
    return null;
  }
}

module.exports = {
  implementation: HTMLLegendElementImpl
};
