"use strict";

const { nodeRoot } = require("jsdom/lib/jsdom/living/helpers/node");
const { mixin } = require("jsdom/lib/jsdom/utils");

const DocumentFragment = require("jsdom/lib/jsdom/living/nodes/DocumentFragment-impl").implementation;
const DocumentOrShadowRootImpl = require("jsdom/lib/jsdom/living/nodes/DocumentOrShadowRoot-impl").implementation;
const InnerHTMLImpl = require("jsdom/lib/jsdom/living/domparsing/InnerHTML-impl").implementation;

class ShadowRootImpl extends DocumentFragment {
  constructor(globalObject, args, privateData) {
    super(globalObject, args, privateData);

    const { mode } = privateData;
    this._mode = mode;
    this._availableToElementInternals = false;
  }

  _getTheParent(event) {
    if (!event.composed && this === nodeRoot(event._path[0].item)) {
      return null;
    }

    return this._host;
  }

  get mode() {
    return this._mode;
  }

  get host() {
    return this._host;
  }
}

mixin(ShadowRootImpl.prototype, DocumentOrShadowRootImpl.prototype);
mixin(ShadowRootImpl.prototype, InnerHTMLImpl.prototype);

module.exports = {
  implementation: ShadowRootImpl
};
